import React, { Component } from "react";
import { Star, Youtube } from "react-feather";
import data from "./public/data.js";

export default class CourseListItem extends Component {

  render() {
    const {title, instructor, shortDescription, image} = this.props;
    return (
      <div className="card col-md-10 offset-md-1 p-0">
        <div className="row no-gutters">
          <div className="col-md-2 bg-danger">
            <img
              src="/medstar-health.png"
              className="card-img"
              alt="course alt text"
            />
          </div>
          <div className="col-md-10">
            <div className=".container-fluid">
              <div className="card-body p-2">
                <h5 className="card-title mb-0">{data[0].title}</h5>
                <p className="card-text">Instructor Name</p>
                <div className="row align-items-end">
                  <p className="card-text col-3 mb-0">
                    <Star size={15} />{' '}
                    <Youtube size={15} />
                  </p>
                  <p className="card-text courseListItem shortDescription col-9">
                    This is a wider card with supporting text below as a natural
                    lead-in to additional content. This content is a little bit
                    longer.
                  </p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    );
  }
}
