import React, { Component } from "react";
import Checkbox from "./Checkbox";
import {filterCatagories} from '../data/filtercatagorydata';

export default class ChoiceContainer extends Component {
  constructor(props) {
    super(props);
    this.state = {
      checkedItems: new Map()
    }
  }

  onCheckboxChange = event => {
    const item = event.target.name;
    const isChecked = event.target.checked;
    this.setState(prevState => ({
      checkedItems: prevState.checkedItems.set(item, isChecked)
    }));
    console.log(this.state.checkedItems)
  };

  render() {
    const checkboxes = this.props.catagoryData.map((item) => {
      return (
          <li className="list-group-item" key={item.key}>
            <label>
              <Checkbox
                name={item.name}
                checked={this.state.checkedItems.get(item.name)}
                onChange={this.onCheckboxChange}
              />
              <span className="pl-2">{item.label}</span>
            </label>
          </li>
      );

    });
    return <ul className="list-group">{checkboxes}</ul>;
  }
}
