import React, { Component } from "react";
import FilterItem from './FilterItem';
import { filterCatagories } from "../data/filtercatagorydata";

export default class Filter extends Component {
  static defaultProps = {
    numOfCatagoriesToShow: 3,
  };

  render() {
     const catagoryContainers = filterCatagories.map((catagory) => {
       return <FilterItem catagoryData={catagory} />;
     });
    return (
      <div className="row px-5">
        <ListOfShownCatagories
          listOfAllCatagories={filterCatagories}
          numOfCatagoriesToShow={this.props.numOfCatagoriesToShow}
        />
      </div>
    );
  }  
}

const ListOfShownCatagories = (props) =>
  props.listOfAllCatagories.reduce((shownCatagories, catagory, index) => {
    if (index < props.numOfCatagoriesToShow) {
      shownCatagories.push(<FilterItem catagoryData={catagory} />);
    }
    return shownCatagories;
  }, []);
