import React, { Component } from 'react';
import Layout from "../components/Layout";
import SearchBar from '../components/SearchBar';
import SearchResults from '../components/SearchResults';
import Filter from "../components/Filter";
import { filterCatagories } from "../data/filtercatagorydata";
import FilterButton from '../components/FilterButton';

export default class Search extends Component {
  state = {
    searchResults: null,
    filterCatagories: filterCatagories,
    numOfCatagoriesToShow: 3,
  }

  submitSearchValue = text => {
    console.log(text);
  }

  onFilterButtonClick = event => {
    const {numOfCatagoriesToShow, filterCatagories} = this.state;
    event.preventDefault();
    numOfCatagoriesToShow - 1 === filterCatagories.length ? this.setState({ numOfCatagoriesToShow: 3 }) :
    this.setState({ numOfCatagoriesToShow: this.state.filterCatagories.length });
  }

  render() {
    return (
      <Layout title="Course Search">
        <div className="divider" />
        <h3 className="pl-3">Find Your Next Course</h3>
        <div className="row align-items-center justify-content-center">
          <div className="col-md-7 pl-5">
            <SearchBar
              submitSearchValue={(text) => this.submitSearchValue(text)}
            />
          </div>
          <div className="col-md-5 pl-5">
            <FilterButton onClick={this.onFilterButtonClick} />
          </div>
        </div>
        <Filter
          filterCatagories={filterCatagories}
          numOfCatagoriesToShow={this.state.numOfCatagoriesToShow}
        />
        <SearchResults />
      </Layout>
    );
  }


}
