import React, { Component } from 'react';
import Layout from "../components/Layout";
import SearchBar from '../components/SearchBar';
import SearchResults from '../components/SearchResults';
import Filter from "../components/Filter";
import { filterCatagories } from "../data/filtercatagorydata";
import { Search as SearchIcon } from "react-feather";

export default class Search extends Component {
  state = {
    searchResults: null,
    filterCatagories: filterCatagories,
    numOfCatagoriesToShow: 3,
  }
  submitSearchValue = text => {
    console.log(text);
  }

  render() {
    return (
      <Layout title="Course Search">
        <div className="divider" />
        <h3 className="pl-3">Find Your Next Course</h3>
        <div className="row align-items-center justify-content-center">
          <div className="col-md-7 pl-5">
            <SearchBar
              submitSearchValue={(text) => this.submitSearchValue(text)}
            />
          </div>
          <div className="col-md-5 pl-5 offset-sm-2">
            <div className="row">
              <div className="col-auto p-0 align-self-end">
                <img
                  src="/filtericon.png"
                  className="img-fluid"
                  alt="filter catagories icon"
                />
              </div>
              <div className="col p-0 align-self-start align-self-center">
                <p className="m-0 pl-2">See All Filter Options</p>
              </div>
            </div>
          </div>
        </div>
        <Filter
          filterCatagories={filterCatagories}
          numOfCatagoriesToShow={this.state.numOfCatagoriesToShow}
        />
        <SearchResults />
      </Layout>
    );
  }


}
